import 'package:flutter/material.dart';import 'package:flutter_app2/layout/shared/shared_layouts.dart';import 'package:flutter_app2/models/categories_model/Category.dart';class CategoryItem extends StatelessWidget {  CategoryModel catgoryModel;  CategoryItem(this.catgoryModel);  @override  Widget build(BuildContext context) {    return Card(     semanticContainer: false,      margin: EdgeInsets.only(bottom: 5),      child: Container(        width: double.infinity,        color: Colors.white,        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            getImageWithCurve(              url:  catgoryModel.image,              context: context,              height_: 100,              width_: 100            ),            Flexible(              flex: 1,                child: Padding(              padding: const EdgeInsets.all(5.0),              child: Text(                catgoryModel.name,                overflow: TextOverflow.ellipsis,                textAlign: TextAlign.end,                maxLines: 1,                style: TextStyle(                  fontFamily: 'Cairo_bold',                  color: Colors.black,                  fontSize: 14,                ),              ),            )),          ],        ),      ),    );  }}