import 'package:flutter/material.dart';import 'package:flutter_app2/layout/home/seeAll_view.dart';import 'package:flutter_app2/layout/products/product_item_light.dart';import 'package:flutter_app2/models/products/products_model.dart';import 'package:flutter_app2/module/product_details/product_details_screen.dart';import 'package:flutter_app2/utiles/utilies.dart';class BestSeller extends StatelessWidget {  bool isLight;  String title;  Function function;  List<ProductModel> list=[];  BestSeller({@required this.isLight, @required this.title,this.list,this.function});  @override  Widget build(BuildContext context) {    return Container(      child: Column(children: [        SeeAllView(          title: '$title',          isLight: isLight,          function: function,        ),        Container(          height: 220,          color: isLight ? Colors.white : Colors.black,          child: ListView.builder(            shrinkWrap: true,            itemBuilder: (context, index) => GestureDetector(child: ProductItemLight(isLight, false,list[index]),            onTap: (){              Navigator.push(                context,                  navigateTo_(ProductDetailsScreen(),settings: RouteSettings(                    arguments:                    list[index].productId,                  )),              );            },),            itemCount: list.length,            scrollDirection: Axis.horizontal,          ),        )      ]),    );  }}