import 'package:flutter/material.dart';import 'package:flutter_app2/layout/home/seeAll_view.dart';import 'package:flutter_app2/layout/home/vendor_item.dart';import 'package:flutter_app2/models/home/vendor_model.dart';class BrandsLayout extends StatelessWidget {  List<VendorItem> vendorsList=[];  BrandsLayout(this.vendorsList);  @override  Widget build(BuildContext context) {    return Container(      width: double.infinity,      child: Column(        children: [          SeeAllView(            title: 'Brands',            function: () {},          ),          Container(              color: Colors.white,              child: GridView.builder(                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(                    crossAxisCount: 3,                    childAspectRatio: 1/1.1,// width/height                 ),                shrinkWrap: true,                itemBuilder: (context, index) => VendorItemWidget(vendorsList[index]),                itemCount: vendorsList.length,                scrollDirection: Axis.vertical,                physics: NeverScrollableScrollPhysics(),              )),        ],      ),    );  }}