import 'package:flutter/material.dart';import 'package:flutter_app2/layout/home/best_seller_layout.dart';import 'package:flutter_app2/layout/home/brands_layout.dart';import 'package:flutter_app2/layout/home/vendor_item.dart';import 'package:flutter_app2/layout/shared/shared_layouts.dart';import 'package:flutter_app2/models/home/category_with_products_model.dart';import 'package:flutter_app2/module/products/products_screen.dart';import 'package:flutter_app2/utiles/utilies.dart';class CategoryWithProductsView extends StatelessWidget {  List<HomePageCategoryWithProducts> models;  CategoryWithProductsView(this.models);  @override  Widget build(BuildContext context) {    return Container(         width: double.infinity,        color: Colors.white,        child: ListView.builder(          shrinkWrap: true,          itemBuilder: (context, index) => getcategoryItem(context,models[index]),          itemCount: models.length,          scrollDirection: Axis.vertical,          physics: NeverScrollableScrollPhysics(),        )    );  }  Widget getcategoryItem(BuildContext context, HomePageCategoryWithProducts model){    return Column(      children: [        getImageWithCurve(            url: model.imageURL, context: context, curve: 8.0, height_: 250),        SizedBox(height: 8,),        BestSeller(          isLight: true,          title: model.name,          list: model.products,          function: () {            Navigator.push(                context,                navigateTo_(ProductsScreen(),settings: RouteSettings(                  arguments:                  model.id,                )),            );          },        )      ],    );  }}