import 'package:carousel_slider/carousel_slider.dart';import 'package:flutter/material.dart';import 'package:flutter_app2/layout/shared/shared_layouts.dart';import 'package:flutter_app2/models/home/slider_model_entity.dart';class ImagesSlider extends StatelessWidget {  List<SliderItem> sliderList = [];  ImagesSlider(this.sliderList);  @override  Widget build(BuildContext context) {    return Container(        width: double.infinity,        height: 250,        child: PageView.builder(          itemBuilder: (context, index) => Padding(            padding: EdgeInsets.symmetric(horizontal: 5),            child: getImageWithCurve(                url: sliderList[index].ImageURL,                context: context,                curve: 8.0,                height_: 250),          ),          itemCount: sliderList.length,          scrollDirection: Axis.horizontal,        ));  }}