import 'dart:ffi';import 'package:flutter/material.dart';import 'package:flutter_app2/layout/home/seeAll_view.dart';import 'package:flutter_app2/layout/home/vendor_item.dart';import 'package:flutter_app2/layout/products/product_item_light.dart';import 'package:flutter_app2/models/home/vendor_model.dart';class PopularCategoryView extends StatelessWidget {  List<VendorItem> vendorsList=[];  PopularCategoryView(this.vendorsList);  @override  Widget build(BuildContext context) {    return      Container(      width: double.infinity,      child: Column(        children: [          SeeAllView(            title: 'Vendors',            function: () {},          ),          Container(            height: 120,              width: double.infinity,              color: Colors.white,              child: ListView.builder(            shrinkWrap: true,            itemBuilder: (context, index) => VendorItemWidget(this.vendorsList[index]),            itemCount: vendorsList.length,            scrollDirection: Axis.horizontal,          )),        ],      ),    );  }}