import 'package:flutter/material.dart';class SeeAllView extends StatelessWidget {  String title;  Function function;  bool isLight;  SeeAllView({@required this.title, this.function,this.isLight=true});  @override  Widget build(BuildContext context) {    return Row(      mainAxisAlignment: MainAxisAlignment.spaceBetween,      children: [        Text(          title,          style: TextStyle(              fontSize: 15, color: isLight?Colors.black:Colors.white, fontFamily: "Cairo_bold"),        ),        Visibility(            visible: function == null ? false : true,            child: TextButton(              child: Text(                'See All',                style: TextStyle(                    fontSize: 13,                    color: Colors.black,                    fontFamily: "Cairo_regular"),              ),              onPressed: function,            ))      ],    );  }}