import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_app2/layout/shared/shared_layouts.dart';import 'package:flutter_app2/models/home/vendor_model.dart';class VendorItemWidget extends StatelessWidget {  VendorItem vendorItem;  VendorItemWidget(this.vendorItem);  @override  Widget build(BuildContext context) {    return Container(      width: 100,      child: Card(        margin: EdgeInsets.all(3),        child: Padding(          padding: EdgeInsets.all(8),          child: Column(            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              Expanded(                flex: 1,                child: getImageWithCurve(                  url:vendorItem.image,                   context: context,                  curve: 4.0,                  fit_cover: false                ),              ),              Center(                child: Text(                  vendorItem.name,                  maxLines: 1,                  overflow: TextOverflow.ellipsis,                  style: TextStyle(                    fontFamily: 'Cairo_regular',                    color: Colors.black,                    fontSize: 13,                  ),                ),              ),            ],          ),        ),      ),    );  }}