import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_app2/layout/shared/shared_layouts.dart';import 'package:flutter_app2/models/products/products_model.dart';class ProductItemLight extends StatelessWidget {  bool isLight;  bool isVertical;  ProductModel productModel;  ProductItemLight(this.isLight, @required this.isVertical, this.productModel);  @override  Widget build(BuildContext context) {    return Container(      width: isVertical          ? MediaQuery.of(context).size.width          : MediaQuery.of(context).size.width / 2.2,      color: isLight ? Colors.white : Colors.black,      child: Padding(        padding: EdgeInsets.all(8),        child: Column(          crossAxisAlignment: CrossAxisAlignment.center,          mainAxisAlignment: MainAxisAlignment.start,          children: [            Expanded(              child: Stack(                children: [                  getImageWithCurve(                      url: productModel.image, context: context, curve: 8.0)                  ,                  if (productModel.flag!=null&&productModel.flag.isNotEmpty)                    Container(                      decoration: BoxDecoration(                        borderRadius: BorderRadius.circular(5.0),                        color: Colors.green[500],                      ),                      margin: EdgeInsets.all(5),                      padding: EdgeInsets.symmetric(horizontal: 5,vertical: 2),                      child: Text(                        productModel.flag,                        maxLines: 1,                        overflow: TextOverflow.ellipsis,                        style: TextStyle(                            fontFamily: 'Cairo_bold',                            color: Colors.white,                            fontSize: 14,                        ),                      ),                    ),                ],              ),            ),            SizedBox(              height: 5,            ),            Container(              width: MediaQuery.of(context).size.width,              child: Text(                productModel.productName,                maxLines: 1,                overflow: TextOverflow.ellipsis,                style: TextStyle(                  fontFamily: 'Cairo_regular',                  color: isLight ? Colors.black : Colors.white,                  fontSize: 12,                ),              ),            ),            Container(                width: MediaQuery.of(context).size.width / 2,                child: Row(                  crossAxisAlignment: CrossAxisAlignment.center,                  mainAxisAlignment: MainAxisAlignment.start,                  children: [                  Expanded(                  flex: 1,                  child:                Text(                  '${productModel.price}  ',                  maxLines: 1,                  overflow: TextOverflow.ellipsis,                  style: TextStyle(                    fontFamily: 'Cairo_bold',                    color: Colors.red,                    fontSize: 12,                  ),                )                  ),                SizedBox(                  width: 8,                ),                Text('${productModel.oldPrice} ${productModel.currency}',                    maxLines: 1,                    overflow: TextOverflow.ellipsis,                    style: TextStyle(                        fontFamily: 'Cairo_regular',                        fontSize: 10,                        color: Colors.grey,                        decoration: TextDecoration.lineThrough))                  ],                )            ),          ],        ),      ),    );  }}