import 'package:flutter/cupertino.dart';Widget getImageWithCurve(    {@required String url,    @required BuildContext context,    double curve = 0,    double height_ = null,    double width_ = null,    bool fit_cover = true}) {  return ClipRRect(      borderRadius: BorderRadius.circular(curve),      child: FadeInImage(          fit: fit_cover ? BoxFit.cover : BoxFit.contain,          width: width_ ?? MediaQuery.of(context).size.width,          height: height_,          placeholder: AssetImage('assets/images/placeholder.png'),          image: NetworkImage(url)));}