 class CategoryModel {   String id;   String name;   String image;   List<CategoryModel> subCategory;  CategoryModel(this.name, this.image,this.id, this.subCategory);factory CategoryModel.fromJson(Map<String, dynamic> json){  return CategoryModel(json['Name'],json['ImageURL'],json['Id'],      List.from(json['SubCategory']).map((item)=>CategoryModel.fromJson(item)).toList());}}