import 'package:flutter_app2/models/products/products_model.dart';class NewProductsModel{  List<ProductModel> list=[];  NewProductsModel(this.list);  factory NewProductsModel.fromJson(Map json){    return	NewProductsModel(List.from(json['NewProducts']).map((item)=>ProductModel.fromJson(item)).toList());  }}class BesSellerProductsModel{  List<ProductModel> list=[];  BesSellerProductsModel(this.list);  factory BesSellerProductsModel.fromJson(Map json){    return	BesSellerProductsModel(List.from(json['BestSeller']).map((item)=>ProductModel.fromJson(item)).toList());  }}class ProductsModel{  List<ProductModel> list=[];  ProductsModel(this.list);  factory ProductsModel.fromJson(Map json){    return	ProductsModel(List.from(json['Products']).map((item)=>ProductModel.fromJson(item)).toList());  }}