import 'package:flutter_app2/models/products/products_model.dart';class ProductDetailsModel{  String returnPolicy;  String name;  String id;  String sku;  String price;  int stock;  String description;  String sizeGuide;  String productURL;  List<ProductModel> relatedProducts;  List<ProductImageModel> image;  List<ProductAttributesModel> productAttributes;  ProductDetailsModel(      this.returnPolicy,      this.name,       this.sku,      this.price,      this.stock,      this.description,      this.sizeGuide,      this.relatedProducts,      this.image,      this.productAttributes,      this.productURL);  factory ProductDetailsModel.fromJson(Map json){    return ProductDetailsModel(        json['ReturnPolicy'],        json['Name'],        json['Sku'],        json['ProductPrice']['Price'],        json['StockQuantity'],        json['FullDescription'],        json['SizeGuide'],        List.from(json['RelatedProducts']).map((item)=>ProductModel.fromJson(item)).toList(),        List.from(json['PictureModels']).map((item)=>ProductImageModel.fromJson(item)).toList(),        List.from(json['ProductAttributes']).map((item)=>ProductAttributesModel.fromJson(item)).toList(),        json['ProductURL'],    );  }}class ProductImageModel{  String imageURL;  ProductImageModel(this.imageURL);  factory ProductImageModel.fromJson(Map<String,dynamic> json){    return ProductImageModel(json['ImageUrl']);  }}class ProductAttributesModel{  String productAttributeId;  String name;  String type;  List<ValuesModel> values;  ProductAttributesModel(      this.productAttributeId, this.name, this.type, this.values);  factory ProductAttributesModel.fromJson(Map json){    return ProductAttributesModel(      json['ProductAttributeId'],      json['Name'],      json['AttributeControlType'],      List.from(json['Values']).map((item)=>ValuesModel.fromJson(item)).toList()    );  }}class ValuesModel{  String name;  String color;  String id;  ValuesModel(this.name, this.color, this.id);  factory ValuesModel.fromJson(Map json){    return ValuesModel(      json['Name'],      json['ColorSquaresRgb'],      json['Id']    );  }}