class ProductModel {  String productId;  String image;  String productName;  String flag;  String price;  double oldPrice;  String currency;  bool hasDiscount;  dynamic discountValue;  ProductModel(      this.productId,      this.image,      this.productName,      this.flag,      this.price,      this.oldPrice,      this.currency,      this.hasDiscount,      this.discountValue);  factory ProductModel.fromJson(Map<String, dynamic> json) {    return ProductModel(        json['ProductId'],        json['ProductImage'],        json['ProductName'],        json['Flag'],        json['ProductPrice'],        json['OldPrice'],        json['Currency'],        json['hasDiscount'],        json['discountValue']);  }}