import 'package:bloc/bloc.dart';import 'package:flutter_app2/models/products/product_details_model.dart';import 'package:flutter_app2/module/product_details/cubit/product_details_states.dart';import 'package:flutter_app2/network/dio_helper/dioHelper.dart';import 'package:flutter_app2/utiles/endpoints.dart';class ProductDetailsCubit extends Cubit<ProductDetailsStates>{  ProductDetailsCubit() : super(InitialState());  ProductDetailsModel productModel;  void getProductDetails(String productId) {    emit(LoadingState());    var quary={"productId":productId};    DioHelper.getData(EndPoints.productDetailsAPI,queries: quary).then((value) {      ProductDetailsModel model = ProductDetailsModel.fromJson(value.data);       productModel=model;      print(model.price);      emit(ProductDetailState());    }).catchError(onError);  } }