import 'package:flutter/material.dart';import 'package:flutter_app2/models/products/product_details_model.dart';class CheckboxesView extends StatefulWidget {  ProductAttributesModel model;  CheckboxesView(this.model){}  @override  _CheckboxesViewState createState() => _CheckboxesViewState();}bool isChecked=false;class _CheckboxesViewState extends State<CheckboxesView> {  @override  Widget build(BuildContext context) {    return Row(      children: [ Checkbox(        onChanged: (value) {          setState(() {            isChecked=value;          });        } ,        value: isChecked,        activeColor: Colors.red,      )        , Text(widget.model.name)],    );  }}