import 'package:flutter/material.dart';import 'package:flutter_app2/models/products/product_details_model.dart';class DropDownListVew extends StatefulWidget {  ProductAttributesModel model;  DropDownListVew(this.model) {  }  @override  _DropDownListVewState createState() => _DropDownListVewState(model);}class _DropDownListVewState extends State<DropDownListVew> {  ProductAttributesModel model;  String dropdownValue=null;  _DropDownListVewState(this.model);  @override  Widget build(BuildContext context) {    if (dropdownValue==null) {      dropdownValue = model.values[0].name;    }    return Container(      child: dropDownList(),    );  }  Widget dropDownList() {    var list = model.values.map((e) => e.name);    print(list);    return DropdownButton<String>(      hint: Text(model.name),      isExpanded: true,      value: dropdownValue,      icon: const Icon(Icons.keyboard_arrow_down),      iconSize: 20,      elevation: 16,      style: const TextStyle(color: Colors.black),      underline: Container(        height: 1,        color: Colors.grey,      ),      onChanged: (value) {        setState(() {          dropdownValue = value;        });      },      items: list.map<DropdownMenuItem<String>>((String value) {        return DropdownMenuItem<String>(          value: value,          child: Text(value),        );      }).toList(),    );  }}