import 'package:flutter/material.dart';import 'package:flutter_app2/models/products/product_details_model.dart';class TextBoxView extends StatelessWidget {  ProductAttributesModel model;  int lines = 1;  TextBoxView(this.model, {bool isSingleLine = true}) {    if (!isSingleLine) {      lines = 4;    }  }  @override  Widget build(BuildContext context) {    return Container(      width: double.infinity,      child: TextFormField(          maxLines: lines,          onChanged: (value) {},          decoration: InputDecoration(              hintText: model.name,               hintStyle: TextStyle(                  color: Colors.black54,                  fontFamily: 'Cairo_regular',                  fontSize: 11),            enabled:true          )),    );  }}