import 'package:flutter/cupertino.dart';import 'package:shared_preferences/shared_preferences.dart';class CacheHelper {  static SharedPreferences sharedPreferences;  static init() async {    sharedPreferences = await SharedPreferences.getInstance();  }  static Future<bool> saveDataString(      {@required String key, @required String value}) async {    return await sharedPreferences.setString(key, value);  }  static String getDataString({@required String key}) {    var value =sharedPreferences.getString(key);    if (value!=null) {      return value;    }    return "No";  }}