import 'package:dio/dio.dart';import 'package:flutter/cupertino.dart';import 'file:///E:/androidProjects/myProojects/flutter_app2/lib/main.dart';import 'package:flutter_app2/utiles/utilies.dart';class DioHelper {  static Dio dio;  static init() {    dio = Dio(BaseOptions(        baseUrl: "https://markaato.com/", receiveDataWhenStatusError: true));  }  static Future<Response> getData(@required String url, { dynamic queries}) async {    var map = {"langId": langId, "currencyId": currencyId, "storeId": storeId};    if (queries!=null) {      map.addAll(queries);    }    print(url);    print(map);    return await dio.get(url, queryParameters: map);  }  static Future<Response> postData(String url, dynamic queries) async {    return await dio.post(url, queryParameters: queries);  }}